src/core/CrossChainLendingVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "../interfaces/IVault.sol";
import "../interfaces/ILendingPool.sol";

/**
 * @title CrossChainLendingVault
 * @dev Main vault contract that manages user deposits and automated rebalancing
 */
contract CrossChainLendingVault is IVault, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    
    // Constants
    uint256 private constant BASIS_POINTS = 10000;
    uint256 private constant MIN_DEPOSIT = 1e16; // 0.01 ETH minimum
    uint256 private constant MAX_ALLOCATION = 9000; // 90% max allocation to single pool
    
    // State variables
    IERC20 public immutable asset;
    ILendingPool public poolA; // Aave Pool
    ILendingPool public poolB; // Compound Pool
    address public yieldMonitor;
    
    // Vault state
    uint256 public totalDeposits;
    uint256 public totalShares;
    uint256 public sharesPerToken;
    mapping(address => UserInfo) public users;
    
    // Pool allocations
    PoolAllocation[] public poolAllocations;
    
    // Configuration
    uint256 public rebalanceThreshold; // In basis points
    uint256 public rebalancePercentage; // Percentage of allocation to rebalance
    bool public autoRebalanceEnabled;
    
    // Events
    event ConfigurationUpdated(string parameter, uint256 oldValue, uint256 newValue);
    event EmergencyWithdraw(address indexed user, uint256 amount);
    event YieldMonitorUpdated(address indexed oldMonitor, address indexed newMonitor);
    
    constructor(
        address _asset,
        address _poolA,
        address _poolB,
        address _yieldMonitor
    ) Ownable(msg.sender) {
        asset = IERC20(_asset);
        poolA = ILendingPool(_poolA);
        poolB = ILendingPool(_poolB);
        yieldMonitor = _yieldMonitor;
        
        // Initialize with 50/50 allocation
        poolAllocations.push(PoolAllocation({
            poolAddress: _poolA,
            percentage: 5000, // 50%
            isActive: true
        }));
        
        poolAllocations.push(PoolAllocation({
            poolAddress: _poolB,
            percentage: 5000, // 50%
            isActive: true
        }));
        
        rebalanceThreshold = 50; // 0.5%
        rebalancePercentage = 1000; // 10%
        autoRebalanceEnabled = true;
        sharesPerToken = 1e18; // 1:1 initially
    }
    
    /**
     * @notice Deposit assets into the vault
     */
    function deposit(uint256 amount) external override nonReentrant {
        require(amount >= MIN_DEPOSIT, "Vault: Amount below minimum");
        
        // Transfer assets from user
        asset.safeTransferFrom(msg.sender, address(this), amount);
        
        // Calculate shares based on current ratio
        uint256 shares = _calculateShares(amount);
        
        // Update user info
        UserInfo storage user = users[msg.sender];
        user.depositedAmount += amount;
        user.vaultShares += shares;
        user.lastDepositTime = block.timestamp;
        
        // Update vault state
        totalDeposits += amount;
        totalShares += shares;
        
        // Allocate funds to pools
        _allocateToPools(amount);
        
        emit Deposited(msg.sender, amount, shares);
    }
    
    /**
     * @notice Withdraw shares from the vault
     */
    function withdraw(uint256 sharesAmount) external override nonReentrant {
        UserInfo storage user = users[msg.sender];
        require(user.vaultShares >= sharesAmount, "Vault: Insufficient shares");
        
        // Calculate underlying amount
        uint256 underlyingAmount = _calculateUnderlying(sharesAmount);
        
        // Update user info
        user.depositedAmount -= underlyingAmount;
        user.vaultShares -= sharesAmount;
        
        // Update vault state
        totalDeposits -= underlyingAmount;
        totalShares -= sharesAmount;
        
        // Deallocate from pools
        _deallocateFromPools(underlyingAmount);
        
        // Transfer assets to user
        asset.safeTransfer(msg.sender, underlyingAmount);
        
        emit Withdrawn(msg.sender, underlyingAmount, sharesAmount);
    }
    
    /**
     * @notice Rebalance funds between pools (called by YieldMonitor)
     */
    function rebalance(address, address fromPool, address toPool, uint256 amount) external {
        require(msg.sender == yieldMonitor, "Vault: Only yield monitor can rebalance");
        require(amount > 0, "Vault: Amount must be > 0");
        
        // Withdraw from source pool
        ILendingPool sourcePool = ILendingPool(fromPool);
        sourcePool.withdraw(address(asset), amount, address(this));
        
        // Deposit to target pool
        ILendingPool targetPool = ILendingPool(toPool);
        asset.approve(address(targetPool), amount);
        targetPool.supply(address(asset), amount, address(this), 0);
        
        emit Rebalanced(fromPool, toPool, amount);
    }
    
    /**
     * @notice Get vault balance across all pools
     */
    function getVaultBalance() external view override returns (uint256) {
        uint256 balanceA = poolA.balanceOf(address(this));
        uint256 balanceB = poolB.balanceOf(address(this));
        return balanceA + balanceB;
    }
    
    /**
     * @notice Get user shares
     */
    function getUserShares(address user) external view override returns (uint256) {
        return users[user].vaultShares;
    }
    
    /**
     * @notice Get current yield data from pools
     */
    function getCurrentYieldData() external view override returns (YieldData memory) {
        (uint256 poolA_Apy, uint256 poolB_Apy) = _getCurrentYields();
        uint256 difference = poolA_Apy > poolB_Apy ? poolA_Apy - poolB_Apy : poolB_Apy - poolA_Apy;
        
        return YieldData({
            poolA_Apy: poolA_Apy,
            poolB_Apy: poolB_Apy,
            lastUpdate: block.timestamp,
            yieldDifference: difference
        });
    }
    
    /**
     * @notice Get pool allocations
     */
    function getPoolAllocations() external view override returns (PoolAllocation[] memory) {
        return poolAllocations;
    }
    
    /**
     * @notice Update rebalance threshold
     */
    function updateRebalanceThreshold(uint256 newThreshold) external onlyOwner {
        require(newThreshold <= 500, "Vault: Threshold too high"); // Max 5%
        uint256 oldThreshold = rebalanceThreshold;
        rebalanceThreshold = newThreshold;
        emit ConfigurationUpdated("rebalanceThreshold", oldThreshold, newThreshold);
    }
    
    /**
     * @notice Update rebalance percentage
     */
    function updateRebalancePercentage(uint256 newPercentage) external onlyOwner {
        require(newPercentage <= 5000, "Vault: Percentage too high"); // Max 50%
        uint256 oldPercentage = rebalancePercentage;
        rebalancePercentage = newPercentage;
        emit ConfigurationUpdated("rebalancePercentage", oldPercentage, newPercentage);
    }
    
    /**
     * @notice Toggle auto rebalancing
     */
    function toggleAutoRebalance() external onlyOwner {
        autoRebalanceEnabled = !autoRebalanceEnabled;
        emit ConfigurationUpdated("autoRebalanceEnabled", autoRebalanceEnabled ? 0 : 1, autoRebalanceEnabled ? 1 : 0);
    }
    
    /**
     * @notice Update yield monitor contract
     */
    function updateYieldMonitor(address newMonitor) external onlyOwner {
        address oldMonitor = yieldMonitor;
        yieldMonitor = newMonitor;
        emit YieldMonitorUpdated(oldMonitor, newMonitor);
    }
    
    /**
     * @notice Emergency withdraw function
     */
    function emergencyWithdraw() external nonReentrant {
        UserInfo storage user = users[msg.sender];
        uint256 userBalance = user.depositedAmount;
        require(userBalance > 0, "Vault: No balance to withdraw");
        
        // Reset user state
        user.depositedAmount = 0;
        user.vaultShares = 0;
        
        // Update vault state
        totalDeposits -= userBalance;
        totalShares -= user.vaultShares;
        
        // Transfer all assets
        asset.safeTransfer(msg.sender, userBalance);
        
        emit EmergencyWithdraw(msg.sender, userBalance);
    }
    
    /**
     * @notice Internal function to calculate shares
     */
    function _calculateShares(uint256 amount) internal view returns (uint256) {
        if (totalShares == 0) {
            return amount; // First depositor gets 1:1 shares
        }
        return (amount * totalShares) / totalDeposits;
    }
    
    /**
     * @notice Internal function to calculate underlying amount
     */
    function _calculateUnderlying(uint256 shares) internal view returns (uint256) {
        if (totalShares == 0) return 0;
        return (shares * totalDeposits) / totalShares;
    }
    
    /**
     * @notice Internal function to allocate funds to pools
     */
    function _allocateToPools(uint256 amount) internal {
        for (uint256 i = 0; i < poolAllocations.length; i++) {
            PoolAllocation storage allocation = poolAllocations[i];
            if (allocation.isActive && allocation.percentage > 0) {
                uint256 poolAmount = (amount * allocation.percentage) / BASIS_POINTS;
                if (poolAmount > 0) {
                    ILendingPool pool = ILendingPool(allocation.poolAddress);
                    asset.approve(address(pool), poolAmount);
                    pool.supply(address(asset), poolAmount, address(this), 0);
                }
            }
        }
    }
    
    /**
     * @notice Internal function to deallocate funds from pools
     */
    function _deallocateFromPools(uint256 amount) internal {
        // Simple implementation - withdraw proportionally from all pools
        for (uint256 i = 0; i < poolAllocations.length; i++) {
            PoolAllocation storage allocation = poolAllocations[i];
            if (allocation.isActive && allocation.percentage > 0) {
                uint256 poolAmount = (amount * allocation.percentage) / BASIS_POINTS;
                if (poolAmount > 0) {
                    ILendingPool pool = ILendingPool(allocation.poolAddress);
                    pool.withdraw(address(asset), poolAmount, address(this));
                }
            }
        }
    }
    
    /**
     * @notice Internal function to get current yields
     */
    function _getCurrentYields() internal view returns (uint256 poolA_Apy, uint256 poolB_Apy) {
        // Aave Pool yield
        ILendingPool.ReserveData memory aaveData = poolA.getReserveData(address(asset));
        poolA_Apy = _rayToApy(aaveData.currentLiquidityRate);
        
        // Compound Pool yield (using the same interface for consistency)
        ILendingPool.ReserveData memory compoundData = poolB.getReserveData(address(asset));
        poolB_Apy = _rayToApy(compoundData.currentLiquidityRate);
    }
    
    /**
     * @notice Convert Ray rate to APY in basis points
     */
    function _rayToApy(uint256 rayRate) internal pure returns (uint256) {
        // Aave's rate is an annualized rate in Ray (27 decimals). To get basis points, divide by 10^23.
        return rayRate / 1e23;
    }
    
    /**
     * @notice Convert block rate to APY in basis points
     */
    function _blockRateToApy(uint256 blockRate) internal pure returns (uint256) {
        // Compound's rate is per block (scaled by 1e18). APY = (rate * blocks_per_year).
        // To get basis points, we multiply by 10000. APY_bp = (blockRate * 2102400 * 10000) / 1e18
        return (blockRate * 2102400) / 1e16;
    }
}

