// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title ILendingPool
 * @dev Interface for lending pool implementations (Aave, Compound, etc.)
 */
interface ILendingPool {
    struct ReserveData {
        uint256 currentLiquidityRate;
        uint256 currentStableBorrowRate;
        uint256 currentVariableBorrowRate;
        uint256 liquidityIndex;
        uint256 variableBorrowIndex;
        uint256 totalLiquidity;
        uint256 totalStableDebt;
        uint256 totalVariableDebt;
        address aTokenAddress;
        address stableDebtTokenAddress;
        address variableDebtTokenAddress;
        uint256 lastUpdateTimestamp;
    }

    function supply(address asset, uint256 amount, address onBehalfOf, uint16 referralCode) external;
    function withdraw(address asset, uint256 amount, address to) external returns (uint256);
    function getReserveData(address asset) external view returns (ReserveData memory);
    function getReserveNormalizedIncome(address asset) external view returns (uint256);
    function supplyRatePerBlock() external view returns (uint256);
    function borrowRatePerBlock() external view returns (uint256);
    function exchangeRateCurrent() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    
    event Supply(address indexed reserve, address user, address indexed onBehalfOf, uint256 amount, uint16 indexed referralCode);
    event Withdraw(address indexed reserve, address user, address indexed to, uint256 amount);
    event InterestRateUpdated(address indexed reserve, uint256 liquidityRate, uint256 stableBorrowRate, uint256 variableBorrowRate);
}

