// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../interfaces/IReactive.sol";
import "../interfaces/ILendingPool.sol";
import "../interfaces/IVault.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title YieldMonitor
 * @dev Reactive contract that monitors lending pool yield rates and triggers rebalancing
 */
contract YieldMonitor is IReactive, Ownable {
    
    /**
     * @dev vmOnly modifier for ReactVM-only functions
     */
    modifier vmOnly() {
        require(address(this) == msg.sender, "vmOnly: caller must be ReactVM");
        _;
    }
    // Constants
    uint256 private constant SEPOLIA_CHAIN_ID = 11155111;
    uint256 private constant CALLBACK_GAS_LIMIT = 200000;
    uint256 private constant REBALANCE_THRESHOLD = 50; // 0.5% in basis points
    uint256 private constant BLOCKS_PER_YEAR_COMPOUND = 2102400;
    uint256 private constant SECONDS_PER_YEAR = 31536000;
    uint256 private constant RAY = 1e27;
    
    // State variables
    address public vaultAddress;
    address public poolA; // Aave Pool
    address public poolB; // Compound Pool
    address public asset;
    bool public isPaused;
    
    // Yield tracking
    uint256 public lastPoolA_Apy;
    uint256 public lastPoolB_Apy;
    uint256 public lastUpdateBlock;
    uint256 public totalAllocated;
    
    // Events
    event YieldRateUpdated(uint256 poolA_Apy, uint256 poolB_Apy, uint256 difference);
    event RebalanceTriggered(address indexed fromPool, address indexed toPool, uint256 amount);
    event MonitorPaused();
    event MonitorResumed();
    
    constructor(
        address _poolA,
        address _poolB,
        address _asset,
        address _vault
    ) Ownable(msg.sender) {
        poolA = _poolA;
        poolB = _poolB;
        asset = _asset;
        vaultAddress = _vault;
        isPaused = false;
        lastUpdateBlock = block.number;
    }
    
    /**
     * @notice Main reactive function that processes incoming events
     */
    function react(LogRecord calldata log) external override vmOnly {
        require(!isPaused, "YieldMonitor: Monitoring is paused");
        
        // Check if this is a rate update event from either pool
        if (log._contract == poolA || log._contract == poolB) {
            _checkAndRebalance();
        }
    }
    
    /**
     * @notice Manually trigger yield check and potential rebalance
     */
    function checkYieldRates() external {
        require(!isPaused, "YieldMonitor: Monitoring is paused");
        _checkAndRebalance();
    }
    
    /**
     * @notice Internal function to check yields and trigger rebalancing if needed
     */
    function _checkAndRebalance() internal {
        (uint256 poolA_Apy, uint256 poolB_Apy) = _getCurrentYields();
        
        lastPoolA_Apy = poolA_Apy;
        lastPoolB_Apy = poolB_Apy;
        lastUpdateBlock = block.number;
        
        uint256 yieldDifference = poolA_Apy > poolB_Apy ? 
            poolA_Apy - poolB_Apy : poolB_Apy - poolA_Apy;
        
        emit YieldRateUpdated(poolA_Apy, poolB_Apy, yieldDifference);
        
        // Trigger rebalancing if difference exceeds threshold
        if (yieldDifference >= REBALANCE_THRESHOLD) {
            _triggerRebalance(poolA_Apy, poolB_Apy);
        }
    }
    
    /**
     * @notice Get current yields from both pools in basis points (10000 = 100%)
     */
    function _getCurrentYields() internal view returns (uint256 poolA_Apy, uint256 poolB_Apy) {
        // Aave Pool yield
        ILendingPool.ReserveData memory aaveData = ILendingPool(poolA).getReserveData(asset);
        poolA_Apy = _rayToApy(aaveData.currentLiquidityRate);
        
        // Compound Pool yield (using the same interface for consistency)
        ILendingPool.ReserveData memory compoundData = ILendingPool(poolB).getReserveData(asset);
        poolB_Apy = _rayToApy(compoundData.currentLiquidityRate);
    }
    
    /**
     * @notice Convert Aave Ray rate to APY in basis points
     */
    function _rayToApy(uint256 rayRate) internal pure returns (uint256) {
        // Aave's liquidity rate is an annualized rate in Ray (1e27).
        // To get basis points (1/10000), we divide by 1e23.
        return rayRate / 1e23;
    }
    
        
    /**
     * @notice Trigger rebalancing by emitting callback event
     */
    function _triggerRebalance(uint256 poolA_Apy, uint256 poolB_Apy) internal {
        address fromPool = poolA_Apy > poolB_Apy ? poolB : poolA;
        address toPool = poolA_Apy > poolB_Apy ? poolA : poolB;
        
        // Calculate rebalance amount (10% of current allocation for safety)
        uint256 rebalanceAmount = _calculateRebalanceAmount(fromPool, toPool);
        
        if (rebalanceAmount > 0) {
            bytes memory payload = abi.encodeWithSignature(
                "rebalance(address,address,uint256)",
                address(0), // ReactVM address (will be replaced)
                fromPool,
                toPool,
                rebalanceAmount
            );
            
            emit Callback(
                SEPOLIA_CHAIN_ID,
                vaultAddress,
                uint64(CALLBACK_GAS_LIMIT),
                payload
            );
            
            emit RebalanceTriggered(fromPool, toPool, rebalanceAmount);
        }
    }
    
    /**
     * @notice Calculate amount to rebalance between pools
     */
    function _calculateRebalanceAmount(address /* fromPool */, address /* toPool */) internal view returns (uint256) {
        // Rebalance 10% of total allocation
        return totalAllocated / 10;
    }
    
    /**
     * @notice Pause monitoring (emergency function)
     */
    function pause() external vmOnly {
        isPaused = true;
        emit MonitorPaused();
    }

    function ownerPause(bool paused) external onlyOwner {
        isPaused = paused;
        if (paused) {
            emit MonitorPaused();
        } else {
            emit MonitorResumed();
        }
    }
    
    /**
     * @notice Resume monitoring
     */
    function resume() external vmOnly {
        isPaused = false;
        emit MonitorResumed();
    }
    
    /**
     * @notice Update vault address
     */
    function setTotalAllocated(uint256 amount) external onlyOwner {
        totalAllocated = amount;
    }

    function updateVaultAddress(address newVault) external onlyOwner {
        vaultAddress = newVault;
    }
    
    /**
     * @notice Get current yield data
     */
    function getCurrentYieldData() external view returns (uint256 poolA_Apy, uint256 poolB_Apy, uint256 difference) {
        (poolA_Apy, poolB_Apy) = _getCurrentYields();
        difference = poolA_Apy > poolB_Apy ? poolA_Apy - poolB_Apy : poolB_Apy - poolA_Apy;
    }
    
    /**
     * @notice Pay function required by IPayer interface
     */
    function pay(uint256 amount) external override {
        // Implementation for payment functionality
        payable(msg.sender).transfer(amount);
    }
    
    // Receive function for ETH payments
    receive() external payable {}
}

