// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../interfaces/ILendingPool.sol";

/**
 * @title MockCompoundPool
 * @dev Mock implementation of Compound V2 cToken for testing
 */
contract MockCompoundPool is ILendingPool {
    address public immutable asset;
    uint256 private _totalSupply;
    uint256 private _exchangeRate;
    mapping(address => uint256) private _balances;
    
    // Compound-specific rates
    uint256 private _supplyRatePerBlock;
    uint256 private _borrowRatePerBlock;
    uint256 private constant BLOCKS_PER_YEAR = 2102400; // ~15 second blocks
    
    constructor(address _asset) {
        asset = _asset;
        _exchangeRate = 1e18; // 1:1 exchange rate initially
        _totalSupply = 0;
        _supplyRatePerBlock = 37893605; // ~5% APY
        _borrowRatePerBlock = 45472326; // ~6% APY
    }
    
    function supply(address _asset, uint256 amount, address onBehalfOf, uint16) external override {
        require(_asset == asset, "MockCompoundPool: Invalid asset");
        require(amount > 0, "MockCompoundPool: Amount must be > 0");
        
        uint256 cTokenAmount = (amount * 1e8) / _exchangeRate;
        _balances[onBehalfOf] += cTokenAmount;
        _totalSupply += cTokenAmount;
        
        emit Supply(_asset, msg.sender, onBehalfOf, amount, 0);
    }
    
    function withdraw(address _asset, uint256 amount, address to) external override returns (uint256) {
        require(_asset == asset, "MockCompoundPool: Invalid asset");
        
        uint256 cTokenAmount = (amount * 1e8) / _exchangeRate;
        require(_balances[msg.sender] >= cTokenAmount, "MockCompoundPool: Insufficient balance");
        
        _balances[msg.sender] -= cTokenAmount;
        _totalSupply -= cTokenAmount;
        
        emit Withdraw(_asset, msg.sender, to, amount);
        return amount;
    }
    
    function getReserveData(address _asset) external view override returns (ReserveData memory) {
        require(_asset == asset, "MockCompoundPool: Invalid asset");
        return ReserveData({
            currentLiquidityRate: _supplyRatePerBlock * 1e9, // Convert to Ray format
            currentStableBorrowRate: 0,
            currentVariableBorrowRate: _borrowRatePerBlock * 1e9,
            liquidityIndex: 1e27,
            variableBorrowIndex: 1e27,
            totalLiquidity: (_totalSupply * _exchangeRate) / 1e8,
            totalStableDebt: 0,
            totalVariableDebt: 0,
            aTokenAddress: address(this),
            stableDebtTokenAddress: address(0),
            variableDebtTokenAddress: address(0),
            lastUpdateTimestamp: block.timestamp
        });
    }
    
    function getReserveNormalizedIncome(address _asset) external view override returns (uint256) {
        require(_asset == asset, "MockCompoundPool: Invalid asset");
        return _exchangeRate * 1e9; // Convert to Ray format
    }
    
    function supplyRatePerBlock() external view override returns (uint256) {
        return _supplyRatePerBlock;
    }
    
    function borrowRatePerBlock() external view override returns (uint256) {
        return _borrowRatePerBlock;
    }
    
    function exchangeRateCurrent() external view override returns (uint256) {
        return _exchangeRate;
    }
    
    function balanceOf(address account) external view override returns (uint256) {
        return (_balances[account] * _exchangeRate) / 1e8;
    }
    
    // Mock functions for testing
    function setSupplyRatePerBlock(uint256 newRate) external {
        uint256 oldRate = _supplyRatePerBlock;
        _supplyRatePerBlock = newRate;
        emit InterestRateUpdated(asset, newRate * 1e9, 0, _borrowRatePerBlock * 1e9);
    }
    
    function setBorrowRatePerBlock(uint256 newRate) external {
        _borrowRatePerBlock = newRate;
        emit InterestRateUpdated(asset, _supplyRatePerBlock * 1e9, 0, newRate * 1e9);
    }
    
    function accrueInterest() external {
        // Mock interest accrual - increase exchange rate
        uint256 interestPerBlock = _supplyRatePerBlock;
        _exchangeRate += (interestPerBlock * _exchangeRate) / (BLOCKS_PER_YEAR * 1e18);
    }
}

